// src/App.js
import React, { useState } from "react";
import './App.css';

const App = () => {
  const [platform, setPlatform] = useState("instagram");
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!url) return alert("Please paste a valid link.");

    setLoading(true);
    setResult(null);

    try {
      const response = await fetch(`http://localhost:3001/api/download?url=${encodeURIComponent(url)}&platform=${platform}`);
      const data = await response.json();
      setResult(data);
    } catch (err) {
      alert("Failed to fetch media");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <h1>üì• Instant Media Downloader</h1>

      <div className="platform-switch">
        <label>Platform:</label>
        <select value={platform} onChange={(e) => setPlatform(e.target.value)}>
          <option value="instagram">Instagram</option>
          <option value="whatsapp">WhatsApp DP</option>
          <option value="youtube">YouTube</option>
        </select>
      </div>

      <div className="input-section">
        <input
          type="text"
          placeholder={`Paste ${platform} URL here...`}
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <button onClick={handleSubmit}>{loading ? "Loading..." : "Preview Media"}</button>
      </div>

      {result && (
        <div className="media-result">
          {result.isVideo && result.videoUrl && (
            <>
              <video controls src={result.videoUrl}></video>
              <div className="center-button">
                <a href={result.videoUrl} download className="download-btn">Download Video</a>
              </div>
            </>
          )}

          {result.imageUrl && (
            <>
              <img src={result.imageUrl} alt="media" />
              <div className="center-button">
                <a href={result.imageUrl} download className="download-btn">Download Image</a>
              </div>
            </>
          )}
        </div>
      )}

      <footer>
        <p>üîê Secure ¬∑ ‚ö° Fast ¬∑ üíØ Free</p>
        <p>¬© 2025 InstantMediaDownloader. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
