import React, { useState } from "react";

const WhatsAppDownloader = () => {
  const [number, setNumber] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFetchDP = async () => {
    if (!number) return alert("Please enter a valid phone number.");
    setLoading(true);
    setResult(null);

    try {
      const res = await fetch(`http://localhost:3001/api/download?url=${encodeURIComponent(number)}&platform=whatsapp`);
      const json = await res.json();
      setResult(json);
    } catch {
      alert("Failed to fetch WhatsApp DP.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="downloader-form">
      <input
        type="text"
        placeholder="Enter WhatsApp number with country code e.g. 919876543210"
        value={number}
        onChange={(e) => setNumber(e.target.value)}
        className="input-box"
      />
      <button onClick={handleFetchDP} className="fetch-button">{loading ? "Loading..." : "Fetch DP"}</button>

      {result && (
        <div className="media-preview">
          {result.imageUrl && (
            <>
              <img src={result.imageUrl} alt="WhatsApp DP" className="media-element" />
              <a href={result.imageUrl} download className="download-btn">Download Image</a>
            </>
          )}
          {result.error && <p className="error">{result.error}</p>}
        </div>
      )}
    </div>
  );
};

export default WhatsAppDownloader;
