// src/DownloaderForm.js
import React, { useState } from "react";
import "./DownloaderForm.css";

function DownloaderForm({ platform }) {
  const [url, setUrl] = useState("");
  const [media, setMedia] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchMedia = async () => {
    if (!url) return alert("Please enter a valid URL.");
    setLoading(true);
    setMedia(null);

    try {
      const res = await fetch(
        `http://localhost:3001/api/download?url=${encodeURIComponent(
          url
        )}&platform=${platform}`
      );
      const json = await res.json();
      setMedia(json);
    } catch (err) {
      alert("Download failed.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="downloader-form">
      <input
        type="text"
        placeholder={`Paste ${platform} URL or Number`}
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        className="input-box"
      />

      <button onClick={fetchMedia} className="fetch-button">
        {loading ? "Loading..." : "Preview Media"}
      </button>

      {media && (
        <div className="media-preview">
          {media.videoUrl && (
            <>
              <video controls src={media.videoUrl} className="media-element" />
              <div className="download-container">
                <a href={media.videoUrl} download className="download-btn">
                  Download Video
                </a>
              </div>
            </>
          )}

          {media.imageUrl && (
            <>
              <img src={media.imageUrl} alt="Preview" className="media-element" />
              <div className="download-container">
                <a href={media.imageUrl} download className="download-btn">
                  Download Image
                </a>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default DownloaderForm;
