import React, { useState } from "react";
import "./DownloaderForm.css";

export default function DownloaderForm({ platform }) {
  const [input, setInput] = useState("");
  const [media, setMedia] = useState(null);
  const [loading, setLoading] = useState(false);

  const onPreview = async () => {
    if (!input.trim()) return alert("Please paste a valid link.");
    setLoading(true);
    setMedia(null);

    try {
      if (platform === "instagram") {
        const res = await fetch(`http://localhost:3001/api/instagram?url=${encodeURIComponent(input)}`);
        const json = await res.json();
        if (!res.ok || json.error) throw new Error(json.error || "Instagram fetch failed");
        setMedia({ ...json, src: json.videoUrl || json.imageUrl, type: json.videoUrl ? "video" : "image" });
      } else if (platform === "youtube") {
        const res = await fetch(`http://localhost:3001/api/youtube/preview?url=${encodeURIComponent(input)}`);
        const json = await res.json();
        if (!res.ok || json.error) throw new Error(json.error || "YouTube preview failed");
        setMedia({ src: json.streamUrl, type: "video" });
      }
    } catch (e) {
      alert(e.message || "Preview failed");
    } finally {
      setLoading(false);
    }
  };

  const onDownload = async () => {
    if (!media) return;
    if (platform === "instagram") {
      // try direct download; if blocked by CORS, use proxy endpoint
      const a = document.createElement('a');
      a.href = `http://localhost:3001/api/instagram/download?src=${encodeURIComponent(media.src)}&filename=instagram`;
      a.setAttribute('download', '');
      document.body.appendChild(a);
      a.click();
      a.remove();
    } else if (platform === "youtube") {
      // go through backend to produce a file and stream it to the user
      const a = document.createElement('a');
      a.href = `http://localhost:3001/api/youtube/download?url=${encodeURIComponent(input)}&title=${encodeURIComponent('InstantSaver')}`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    }
  };

  return (
    <div className="downloader-form">
      <div className="input-row">
        <input
          type="text"
          placeholder={platform === 'instagram' ? "Paste Instagram link (Reel/Post/IGTV)" : "Paste YouTube link (Short/Live/Long)"}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="input-box"
        />
        <button className="btn primary" onClick={onPreview} disabled={loading}>
          {loading ? "Loading..." : "Preview"}
        </button>
      </div>

      {media && (
        <div className="media-preview">
          {media.type === "video" ? (
            <video controls src={media.src} className="media-element" />
          ) : (
            <img src={media.src} alt="Preview" className="media-element" />
          )}

          <div className="download-container">
            <button className="btn success" onClick={onDownload}>Download</button>
          </div>
        </div>
      )}
    </div>
  );
}
