import React, { useState } from "react";

const InstagramDownloader = () => {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleDownload = async () => {
    if (!url.trim()) {
      alert("Please enter a valid Instagram URL");
      return;
    }
    setLoading(true);
    setResult(null);

    try {
      const response = await fetch(`http://localhost:3001/api/download?url=${encodeURIComponent(url)}&platform=instagram`);
      const json = await response.json();
      setResult(json);
    } catch {
      alert("Failed to fetch Instagram media");
    } finally {
      setLoading(false);
    }
  };

  // Client-side download helper
  const triggerDownload = (url, filename) => {
    const a = document.createElement("a");
    a.href = url;
    a.download = filename || "instagram_media";
    document.body.appendChild(a);
    a.click();
    a.remove();
  };

  return (
    <div className="downloader-form">
      <input
        type="text"
        placeholder="Paste Instagram Post/Reel/IGTV URL"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        className="input-box"
      />
      <button onClick={handleDownload} disabled={loading} className="fetch-button">
        {loading ? "Loading..." : "Fetch"}
      </button>

      {result && (
        <div className="media-preview">
          {result.error && <p className="error">{result.error}</p>}

          {result.videoUrl && (
            <>
              <video controls src={result.videoUrl} className="media-element" />
              <button
                className="download-btn"
                onClick={() => triggerDownload(result.videoUrl, "instagram_video.mp4")}
              >
                Download Video
              </button>
            </>
          )}

          {result.imageUrl && (
            <>
              <img src={result.imageUrl} alt="Instagram" className="media-element" />
              <button
                className="download-btn"
                onClick={() => triggerDownload(result.imageUrl, "instagram_image.jpg")}
              >
                Download Image
              </button>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default InstagramDownloader;
