      </header>

      <main>
        {activeTab === "instagram" && <InstagramDownloader />}
        {activeTab === "whatsapp" && <WhatsAppDownloader />}
        {activeTab === "youtube" && <YouTubeDownloader />}
        {activeTab === "faq" && <FAQ />}
      </main>

      <footer>
        <p>Â© 2025 InstantSaver. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
