import React, { useState } from "react";

export default function WhatsAppDownloader() {
  const [number, setNumber] = useState("");
  const [media, setMedia] = useState(null);
  const [loading, setLoading] = useState(false);

  async function fetchDP() {
    if (!number.trim() || !/^\d{8,15}$/.test(number.trim())) {
      alert("Enter valid number with country code");
      return;
    }
    setLoading(true);
    setMedia(null);
    try {
      const res = await fetch(`http://localhost:3001/api/download?platform=whatsapp&url=${encodeURIComponent(number)}`);
      const data = await res.json();
      setMedia(data);
    } catch {
      alert("Fetch failed");
    }
    setLoading(false);
  }

  function downloadFile(url, filename) {
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    a.remove();
  }

  return (
    <div className="downloader-form">
      <input
        placeholder="WhatsApp Number with Country Code e.g. 919860191711"
        value={number}
        onChange={e => setNumber(e.target.value)}
        className="input-box"
      />
      <button onClick={fetchDP} disabled={loading} className="fetch-button">
        {loading ? "Loading..." : "Fetch DP"}
      </button>

      <p style={{fontSize:"14px",color:"#666",marginTop:"5px"}}>
        Note: WhatsApp may not reveal current DP for privacy reasons.
      </p>

      {media && (
        <div className="media-preview">
          {media.error && <p className="error">{media.error}</p>}

          {media.imageUrl && (
            <>
              <img className="media-element" src={media.imageUrl} alt="WhatsApp DP" />
              <button className="download-btn" onClick={() => downloadFile(media.imageUrl, "whatsapp_dp.jpg")}>
                Download Image
              </button>
            </>
          )}
        </div>
      )}
    </div>
  );
}
