import React, { useState } from 'react';
import './DownloaderForm.css';

function DownloaderForm({ platform }) {
  const [url, setUrl] = useState('');
  const [media, setMedia] = useState(null);

  const fetchMedia = async () => {
    try {
      const res = await fetch(`http://localhost:3001/api/download?url=${encodeURIComponent(url)}&platform=${platform}`);
      const json = await res.json();
      setMedia(json);
    } catch (err) {
      alert("Download failed.");
    }
  };

  return (
    <div className="downloader-form">
      <input
        type="text"
        placeholder={`Paste ${platform.charAt(0).toUpperCase() + platform.slice(1)} URL here`}
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        className="input-box"
      />
      <button onClick={fetchMedia} className="fetch-button">
        Download Media
      </button>

      {media && (
        <div className="media-preview">
          {media.videoUrl && (
            <>
              <video controls src={media.videoUrl} className="video-preview" />
              <a href={media.videoUrl} download className="download-link">Download Video</a>
            </>
          )}
          {media.imageUrl && (
            <>
              <img src={media.imageUrl} alt="Preview" className="img-preview" />
              <a href={media.imageUrl} download className="download-link">Download Image</a>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default DownloaderForm;
