import React, { useState } from "react";
import InstagramDownloader from "./components/InstagramDownloader";
import WhatsAppDownloader from "./components/WhatsAppDownloader";
import YouTubeDownloader from "./components/YouTubeDownloader";
import FAQ from "./components/FAQ";

const App = () => {
  const [tab, setTab] = useState("instagram");

  return (
    <div className="app">
      <header>
        <h1>ðŸ“¥ Multi Platform Downloader</h1>
        <nav>
          <button className={tab === "instagram" ? "active" : ""} onClick={() => setTab("instagram")}>Instagram</button>
          <button className={tab === "whatsapp" ? "active" : ""} onClick={() => setTab("whatsapp")}>WhatsApp</button>
          <button className={tab === "youtube" ? "active" : ""} onClick={() => setTab("youtube")}>YouTube</button>
          <button className={tab === "faq" ? "active" : ""} onClick={() => setTab("faq")}>FAQ</button>
        </nav>
      </header>

      <main>
        {tab === "instagram" && <InstagramDownloader />}
        {tab === "whatsapp" && <WhatsAppDownloader />}
        {tab === "youtube" && <YouTubeDownloader />}
        {tab === "faq" && <FAQ />}
      </main>

      <footer>
        <p>Â© 2025 YourBrandName</p>
      </footer>
    </div>
  );
};

export default App;
