import React, { useState } from "react";
import "./DownloaderForm.css";

export default function DownloaderForm({ platform, setPreviewUrl, setPreviewFormat }) {
  const [input, setInput] = useState("");
  const [media, setMedia] = useState(null);
  const [loading, setLoading] = useState(false);

  const normalizeYouTube = (url) => {
    let u = url.trim().split("?")[0].replace(/\/$/, "");
    if (/\/shorts\/([^\/]+)/.test(u))
      return `https://www.youtube.com/watch?v=${u.match(/\/shorts\/([^\/]+)/)[1]}`;
    if (/youtu\.be\/([^\/]+)/.test(u))
      return `https://www.youtube.com/watch?v=${u.match(/youtu\.be\/([^\/]+)/)[1]}`;
    return u;
  };

  const onPreview = async () => {
    if (!input.trim()) return alert("Please paste a valid link.");
    setLoading(true);
    setMedia(null);
    setPreviewUrl("");
    setPreviewFormat("");

    try {
      if (platform === "instagram") {
        const res = await fetch(`http://localhost:3001/api/instagram?url=${encodeURIComponent(input)}`);
        const json = await res.json();
        if (!res.ok || json.error) throw new Error(json.error || "Instagram fetch failed");

        if (json.streamUrl) {
          setMedia({ type: "video", streamUrl: json.streamUrl, username: json.username || null });
          setPreviewUrl(json.streamUrl);
          setPreviewFormat("mp4");
        } else if (json.imageUrl) {
          setMedia({ type: "image", src: json.imageUrl, username: json.username || null });
          setPreviewUrl(json.imageUrl);
          setPreviewFormat("jpg");
        }
      } else if (platform === "youtube") {
        const url = normalizeYouTube(input);
        const res = await fetch(`http://localhost:3001/api/youtube/preview?url=${encodeURIComponent(url)}`);
        const json = await res.json();
        if (!res.ok || json.error) throw new Error(json.error || "YouTube preview failed");

        if (json.streamUrl) {
          setMedia({ type: "video", streamUrl: json.streamUrl, username: json.username || null });
          setPreviewUrl(json.streamUrl);
          setPreviewFormat("mp4");
        } else {
          setMedia(null); // no preview available
        }
      }
    } catch (e) {
      alert(e.message || "Preview failed");
    } finally {
      setLoading(false);
    }
  };

  const onDownload = () => {
    if (!media) return;

    if (platform === "instagram") {
      const a = document.createElement("a");
      a.href = `http://localhost:3001/api/instagram/download?src=${encodeURIComponent(media.streamUrl || media.src)}&filename=instagram`;
      a.setAttribute("download", "");
      document.body.appendChild(a);
      a.click();
      a.remove();
    } else if (platform === "youtube") {
      const a = document.createElement("a");
      a.href = `http://localhost:3001/api/youtube/download?url=${encodeURIComponent(input)}&title=InstantSaver`;
      document.body.appendChild(a);
      a.click();
      a.remove();
    }
  };

  return (
    <div className="downloader-form">
      <div className="input-row">
        <input
          type="text"
          placeholder={platform === "instagram" ? "Paste Instagram link" : "Paste YouTube link"}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="input-box"
        />
        <button className="btn primary" onClick={onPreview} disabled={loading}>
          {loading ? "Loading..." : "Preview"}
        </button>
      </div>

      {media && (
        <div className="media-preview">
          {media.type === "video" ? (
            <video controls className="media-element">
              <source src={media.streamUrl} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          ) : (
            <img src={media.src} alt="Preview" className="media-element" />
          )}

          {media.username && <p className="username">Posted by @{media.username}</p>}

          <div className="download-container">
            <button className="btn success" onClick={onDownload}>
              Download
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
