import React, { useState } from "react";

const WhatsAppDownloader = () => {
  const [number, setNumber] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFetch = async () => {
    if (!number.trim() || !/^\d{8,15}$/.test(number.trim())) {
      alert("Please enter a valid WhatsApp number with country code");
      return;
    }
    setLoading(true);
    setResult(null);

    try {
      const response = await fetch(`http://localhost:3001/api/download?url=${encodeURIComponent(number)}&platform=whatsapp`);
      const json = await response.json();
      setResult(json);
    } catch {
      alert("Failed to fetch WhatsApp DP");
    } finally {
      setLoading(false);
    }
  };

  const triggerDownload = (url, filename) => {
    const a = document.createElement("a");
    a.href = url;
    a.download = filename || "whatsapp_dp.jpg";
    document.body.appendChild(a);
    a.click();
    a.remove();
  };

  return (
    <div className="downloader-form">
      <input
        type="text"
        placeholder="Enter WhatsApp Number with Country Code e.g. 919860191711"
        value={number}
        onChange={(e) => setNumber(e.target.value)}
        className="input-box"
      />
      <button onClick={handleFetch} disabled={loading} className="fetch-button">
        {loading ? "Loading..." : "Fetch DP"}
      </button>

      {result && (
        <div className="media-preview">
          {result.error && <p className="error">{result.error}</p>}

          {result.imageUrl && (
            <>
              <img src={result.imageUrl} alt="WhatsApp DP" className="media-element" />
              <button
                className="download-btn"
                onClick={() => triggerDownload(result.imageUrl, "whatsapp_dp.jpg")}
              >
                Download Image
              </button>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default WhatsAppDownloader;
