import React, { useState } from "react";

const YouTubeDownloader = () => {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleDownload = async () => {
    if (!url) return alert("Please enter a valid YouTube URL.");
    setLoading(true);
    setResult(null);

    try {
      const res = await fetch(`http://localhost:3001/api/download?url=${encodeURIComponent(url)}&platform=youtube`);
      const json = await res.json();
      setResult(json);
    } catch {
      alert("Failed to download YouTube video.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="downloader-form">
      <input
        type="text"
        placeholder="Paste YouTube video or shorts URL"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        className="input-box"
      />
      <button onClick={handleDownload} className="fetch-button">{loading ? "Loading..." : "Download"}</button>

      {result && (
        <div className="media-preview">
          {result.videoUrl && (
            <>
              <video controls src={result.videoUrl} className="media-element" />
              <a href={result.videoUrl} download className="download-btn">Download Video</a>
            </>
          )}
          {result.error && <p className="error">{result.error}</p>}
        </div>
      )}
    </div>
  );
};

export default YouTubeDownloader;
