import React, { useState } from 'react';
import './DownloaderForm.css';

function DownloaderForm() {
  const [url, setUrl] = useState('');
  const [media, setMedia] = useState(null);

  const fetchMedia = async () => {
    try {
      const res = await fetch(`http://localhost:3001/api/download?url=${encodeURIComponent(url)}`);
      const json = await res.json();
      setMedia(json);
    } catch (error) {
      alert("Error fetching media");
    }
  };

  return (
    <div className="downloader-form">
      <input
        type="text"
        placeholder="Paste Instagram/WhatsApp URL"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
        className="input-box"
      />
      <button onClick={fetchMedia} className="fetch-button">
        Fetch Media
      </button>

      {media && (
        <div className="media-preview">
          <video controls src={media.videoUrl} className="video-preview" />
          <a href={media.videoUrl} download className="download-link">
            Download
          </a>
        </div>
      )}
    </div>
  );
}

export default DownloaderForm;
